const itemJson = $input.first().json;


const callbackData = itemJson.callback_query
    ?.message
    ?.reply_markup
    ?.inline_keyboard
    ?.[0]?.[0]
    ?.callback_data;

let extractedData = {
    Id: '',
    Site: '',
    Doc: ''
};

if (callbackData) {

    const regex = /^(\d+)(.+?)Doc:(.+)$/;
    const match = callbackData.match(regex);

    if (match) {

        extractedData.Id = match[1];     
        extractedData.Site = match[2].trim(); 
        extractedData.Doc = match[3];     
    }
}


return [{
    json: extractedData
}];

const items = $input.all();


for (const item of items) {

  const data = item.json;



  try {

    if (typeof data.videos === 'string') {

      data.videos = JSON.parse(data.videos);

    }

    if (typeof data.resources === 'string') {

      data.resources = JSON.parse(data.resources);

    }

    if (typeof data.images === 'string') {

      data.images = JSON.parse(data.images);

    }

  } catch (error) {


    if (!Array.isArray(data.videos)) data.videos = [];

    if (!Array.isArray(data.resources)) data.resources = [];

    if (!Array.isArray(data.images)) data.images = [];

  }

}


return items;

